#define SCALE 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = SCALE * (2. * fragCoord - iResolution.xy) / iResolution.y 
              * mat2(1, -1, 1, 1) / sqrt(2.),
     	 gv = fract(uv) - .5,
         id = floor(uv);

    float t = iTime;
    float m = 0.;

    for(int k = 0; k < 9; ++k) {
        vec2 off = vec2(k % 3, k / 3) - 1.;
        float l = length(gv - off) - 1.5 * (sin(t - length(id + off) * .3) * .5 + .5);
        m += (1. - 2. * m) * smoothstep(.7, -.7, l / min(.05, fwidth(l)));
    }

    fragColor = vec4(m);
}
