// please use this code as much as possible :)
// a mention would be generous <3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 10.0;
  	bool check = fract(dot(floor(uv), vec2(.5))) > 0.0;
    // Output to screen
    fragColor = check ? vec4(1.0): vec4(0.0);
}
