void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.y-iResolution.xy) / iResolution.y;

    float t = (iTime+1.3)*1.5;
    float c = .0, d = .0;

    for(float f = 0.; f < 1.2; f += .7)
    {


        if (p.x > .0)


        d = distance(
              vec3( tan(p.x*t), tan(p.y*t), f ),
              vec3( p.x*20., .0, -5. )
            );

    	c += sin(t + .5/p.x + cos(p.y)*15. );
    }

	fragColor = vec4(.5-c, .6-c*.25,1.-c*.5, 2.);//vec4(1.-c);
}
