float sp(vec4 pos)
{
    float r = 1.0;

    for (int i = 0 ; i < 3; ++i)
        r *= ((clamp(length(pos)*0.25 / float(i) - 0.25 / float(i), 0., 1.) - .03)) * 20.;

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x /= iResolution.y / iResolution.x;

    vec4 pos = vec4(uv.x, uv.y, 0., 1.);
    pos.z = length(pos.xy) * sin(iTime);

    pos.xy /= sp(pos);
    pos.z = length(pos.xy);

    vec3 col = vec3(0.5);
    vec3 cb = mod(pos.yxy*sin(iTime) + iTime, 5.0) * 0.3;
    cb.x = 0.;

    col = cb / exp(pos.z * 0.07);

    fragColor = vec4(col,1.0);
}
