vec2 uvtotuv(in vec2 uv){
 	return 0.5*(iResolution.y*uv/iResolution.xy + 1.0);
}

vec2 starpos(vec2 x){
 	return vec2(sin(x.x*iTime), cos(x.y*iTime));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = 2.0*(fragCoord- 0.5*iResolution.xy)/iResolution.y;

    vec2 d = 0.5*sin(0.1*iTime)*vec2(sin(iTime), cos(iTime));

    uv *= 2.0;
    uv -= d;

    vec2  dir   = normalize(uv);
    float theta = length(uv) ;
   	float a     = 0.8;

    vec2 p = dir*(theta - a/theta);

    p += d;

    fragColor = texture(iChannel1, uvtotuv(p));

    vec2 s = 3.0*sin(0.5*iTime)*vec2(1.0, 0.0);

    float df = 1e9;
    df = min(df, length(p - starpos(vec2(0.3, 0.6))));
    df = min(df, length(p - starpos(vec2(0.3, 1.7))));
    df = min(df, length(p - starpos(vec2(0.1, 0.3))));
    df = min(df, length(p - starpos(vec2(0.14, 0.2))));

    df = min(df, length(p - starpos(vec2(0.14, 0.9))));
    df = min(df, length(p - starpos(vec2(0.24, 0.1))));
    df = min(df, length(p - starpos(vec2(0.74, 0.4))));
    df = min(df, length(p - starpos(vec2(0.14, 2.2))));



    fragColor += 1.0 -  smoothstep(0.01, 0.1, df);

    float shade = length(1.5*uv);

    float r = smoothstep(0.2, 0.7, 1.0 - length(uv));
    fragColor *= vec4(1.0 - r*r*r, 1.0 - r , 1.0 - r, 0.0);
}
