// Yellow Circle
// from The Art od Code - Shadertoy Tutorials
// https://www.youtube.com/playlist?list=PLGmrMu-IwbguU_nY2egTFmlg691DN7uE5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; 	// 0. <> 1.

    uv -= .5;									// -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;		// compress ellipse to circle (x axis)

    vec3 color = vec3(1.);

    // change the values above

    float d = length(uv);						// UV -= .5 represents the cemter of the screen
    float r = 0.3;								// radius of the circle
    float blur = 0.01;							// blur amount : higher values for more blur
    vec3 yellow = vec3(1.,1.,0.);		    	// rgb values for yellow : red=1, green=1, blue=0

    float mask = smoothstep(r, r-blur, d);		// create a mask with the shape pf of a blurry circle

    color = yellow*mask;

    fragColor = vec4(color, 1.);				// draw a blurry yellow circle (alpha = 1)
}
