float polygon(in vec2 uv)
{
    float PI = 3.1415926;
    float a = atan(uv.x, uv.y) + PI;
    float r = 2.0 * PI / 5.0;
	float k = floor(0.5 + a / r) * r - a;
    float c = cos(r / 2.0) / cos(r / 2.0 - k) * length(uv);
    if(c < 1.0) c = 0.0;
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - (0.5, 0.5)) * 4.0;
    // Output to screen
    fragColor = vec4(polygon(uv),0,0,1);
}
