void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float balanco2=(sin((100000.0*iTime)+(uv.x*100000.0))/60000.0);


    // Output to screen

    if(uv.y>0.7-balanco2*600000.0)
        fragColor = vec4(1,0,0,1)*1.0-balanco2*6000000.0;







    else if(uv.y<0.4+balanco2*600000.0)
        fragColor = vec4(0,0,1,1)*1.0-balanco2*400000.0;



                else if(uv.y>0.2-balanco2*60000.0)
        fragColor = vec4(1,1,0,1)*1.0-balanco2*30000.0;





                    }
