void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    //Change these values to vary effect
    float factor = 5.0;
    float varyeffect = 35.0;


    //DO NOT CHANGE
    float intensity = log(factor*5.0)/factor*varyeffect;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(cos(uv.x*factor) + sin(-uv.x*factor)); 

    // Time varying pixel color
    if (mod((uv.x+iTime)*100.0, 4.0) < 2.0 && 2.0 < mod((uv.y-iTime)*100.0, 4.0) && mod((uv.y-iTime)*100.0, 4.0) < 6.0){
    	col = ((intensity)*col + vec3(max(0.0, sin((uv.x+(3.14159265))*factor)), max(0.0, -sin((uv.x+(3.14159265))*factor)), max(0.0, cos((uv.x+(3.14159265))*factor))))/(intensity+1.0); 

    }
    //vec3 col = uv.xyx * vec3(pow(1.5, 3.0 + 2.5*cos(iTime)));

        //0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    //DO NOT CHANGE
}
