void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.y;
    //for "Centering" purposes
    st.x = st.x-0.21*iResolution.x/iResolution.y;
    //Initializing the colors we'll need
    vec3 bk_color  = vec3(0.941,0.91,0.85);
    vec3 red_color = vec3(0.694,0.133,0.141);
    vec3 yellow_color = vec3(0.964,0.745,0.129);
    vec3 blue_color = vec3(0.,0.372,.600);

    vec3 color = vec3(0.);
    //Draw the background
    float background = step(0.07, st.x)*step(0.07,1.-st.x);
    //Draw the red region
    float red_region = step(0.07, st.x)*step(1.-0.28,1.-st.x)*step(1.-0.35,st.y);
    //Draw the yellow region
    float yellow_region = step(1.-0.1, st.x)*step(0.07,1.-st.x)*step(1.-0.35,st.y);
    //Draw the blue region
    float blue_region = step(1.-0.07, 1.-st.y)*step(.07,1.-st.x)*step(1.-0.31, st.x);
    //draw the first vetical lines
    float v_line1 = step(1.-0.28,1.-st.x)+step(0.31,st.x);
    float v_line2 = step(1.-0.31,st.x)+step(0.34,1.-st.x);
    float v_line3 = step(1.-0.1,st.x)+step(0.13,1.-st.x);
    float v_line4 = step(1.-0.13,1.-st.x)+step(0.16,st.x)+step(0.35,1.-st.y);
    //Draw the horizontal lines
    float h_line1 = step(1.-0.07,1.-st.y)+step(0.1,st.y)+step(1.-0.28,1.-st.x)+step(1.-0.07,st.x);
    float h_line2 = step(1.-0.35,st.y)+step(0.38,1.-st.y)+step(1.-0.07,1.-st.x)+step(1.-0.07,st.x);
    float h_line3 = step(1.-0.16,st.y)+step(0.19,1.-st.y)+step(1.-0.07,1.-st.x)+step(1.-0.07,st.x);

    float lines = clamp(v_line1*v_line2*v_line3*h_line1*h_line2*h_line3*v_line4,0.,1.);

    color = mix(vec3(1,1,1),bk_color,background);
    color = mix(color,red_color,red_region);
    color = mix(color,yellow_color,yellow_region);
    color = mix(color,blue_color,blue_region);
    color = mix(vec3(0.,0.,0.),color, lines);

    fragColor = vec4(color,1.0);
}
