//Ethan Alexander Shulman 2019, made on livestream at twitch.tv/ethanshulman

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 rd = normalize(vec3((fragCoord*2.-iResolution.xy)/iResolution.x,1));
    if (iMouse.w > 0.) {
        vec2 mc = iMouse.xy*6.28/iResolution.xy;
        rd.yz *= rot(-mc.y);
        rd.xz *= rot(mc.x);
    } else rd.xz *= rot(iTime*.5);
    vec2 pc = rd.xz*(.5/(1.+abs(rd.y)))+iTime*.01;

	vec2 dp = pc*10.+cos((pc.yx-.5)*vec2(16,3)),
        fp = floor(dp*.5),
        rp = mod(dp,2.)-1.;
	float c = max(0.,(.9+.1*hash(fp.xyyx).x)-length(rp)*.8),
	m = 0.;
	for (int i = 0; i < 3; i++) {
		m += max(0.,length(mod(
		pc*(10.+float(i)*10.)+
		cos(float(i+2)/(1.+c*float(1+i))+pc.yx-.5*float(i*2+3)+float(i)*vec2(1.4,.6))*float(1+i*2),
		2.)-1.));
	}
	c *= pow(m*.2,1./4.);
	c = pow(c,2.);
	fragColor = mix(vec4(.1,.5,.9,1),vec4(1),c);
}
