float udBox(in vec2 p, in vec2 b) {
	return length(max(abs(p)-b,0.));
}

#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

void mainImage( out vec4 O, in vec2 u )
{
    O -= O;
    //O -= O++;
    vec2 R = iResolution.xy, p = (u+u-R)/R.y;

    float t = (.5+.5*-cos(iTime*1.7))*3.;

    vec2 s = vec2(.125,.75);
    float d0 = max(abs(abs(p.x)-s.x*2.)-s.x, p.y-(clamp(t-2.*float(p.x>=0.),-.05,1.)*2.-1.)*s.y);
    //O.r += .7*smoothstep(3./R.y,0., );
    //O.r += .7*smoothstep(3./R.y,0.,max(udBox(p+vec2(.3,0),s), p.y-(t*2.-1.)*s.y) );
    //O.r += .7*smoothstep(3./R.y,0.,max(udBox(vec2(p.x-.3,-p.y),s), -(-p.y)-(t*2.-1.)*s.y) );

	float x2 = 2.*s.x;
	float x1 = -2.*s.x;
    float y2 = -s.y;
    float y1 = s.y;
    float a = atan(y2-y1,x2-x1)+3.14;

    float w = s.x*sin(a);

    vec2 p0 = rot(a)*p;
    float d1 = max(abs(p0.y)-w, -(p.y + s.y*((t-1.)*2.-1.)) );
    //float d1 = max(abs(p0.y)-w, -(p0.x + s.y*((t-1.)*2.-1.)) );

    //float d2 = min(d0, d1);

    d0 = max(d0, abs(p.y)-s.y);
    d1 = max(d1, abs(p.y)-s.y);

    float r = 2.8;
    float dx = s.x*3.;
    float d2 = length(p+vec2(0,s.y+sqrt(r*r - dx*dx)))-r;

    d0 = max(d0, -d2 );
    d1 = max(d1, -d2 );

    float d = min(d0,d1);

    //O *= .25*vec4(d);
    O = mix(O, vec4(.6 -.5*exp(-22.*max(d1,0.)) * (1.-pow(abs(p0.x),1.25)),0,0,0), smoothstep(3./R.y, 0., d0));
    //O *= 1. - .8 * ;
    O = mix(O, vec4(1,0,0,0), smoothstep(3./R.y, 0., d1));

    O.rgb = sqrt(O.rgb);
}
