void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Setup
    vec4 solidWhite = vec4(1.0,1.0,1.0,1.0);
    vec4 solidRed = vec4(1.0,0.0,0.0,1.0);
    vec4 solidBlue = vec4(0.0,0.0,1.0,1.0);

    vec4 color = solidWhite;

   	vec2 xy = fragCoord.xy;

    float width = iResolution.x;
    float height = iResolution.y;

	float size = sin(mod(width/10. + iTime*5., 100.))*50.;

    // Square
	/*
    if (
   		xy.x < width/2. + size && 
        xy.x > width/2. - size &&
        xy.y < height/2. + size &&
        xy.y > height/2. - size
    ) {
        color=solidRed;
    }
	*/

    // Circle
    if (
    	pow((xy.x - width/2.), 2.) + pow((xy.y - height/2.), 2.) < pow(size, 2.)
    ) {
		color=solidRed;
    }



    fragColor = color;
}
