float DrawLine(vec2 uv, float thickness)
{
    return  smoothstep(uv.y - thickness, uv.y, uv.x) - 
        	smoothstep(uv.y, uv.y + thickness, uv.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

   	vec2 v = uv;

    // Use of step
    //v.x = step(0.1, v.x);

    // Use of smoothstep
    //v.x = smoothstep(.1, .9, v.x);

    float l = DrawLine(v, 0.02);
    float l2 = DrawLine(vec2(1.-v.x, v.y), 0.02);
    float l3 = DrawLine(vec2(step(1., v.x), v.y-0.5), 0.02);
    float l4 = DrawLine(vec2(step(1., v.y), v.x-0.5), 0.01);


    // Conneting 2 smoothstep together
    //float y = smoothstep(0.2,0.5,uv.y) - smoothstep(0.79,0.8,uv.y);

    //fragColor = vec4(y, 0, 0, 1.0);

    //fragColor = vec4((1.-l) * uv.x, (1.-l) *uv.y, 0, 1.0);

    float a = (smoothstep(0.2, 0.5, uv.y) - smoothstep(0.5, 0.8, uv.y));
    float b = (smoothstep(0.2, 0.5, uv.x) - smoothstep(0.5, 0.8, uv.x));
    fragColor = vec4(l * a, l2 * a, (l3+l4) *b * a, 1.0);
}
