// Rounded rect distance function
float udRoundRect(vec2 p, vec2 b, float r)
{
	return length(max(abs(p) - b, 0.0)) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Rect center and half size


   	vec2 uv = fragCoord / iResolution.xy;


    vec2 center = iResolution.xy * 0.5;

    vec2 hsize = iResolution.xy/2.0;


	//vec2 uv = fragCoord / iResolution.xy;

    float radius =iResolution.x/5.0;

	float a = clamp(udRoundRect(fragCoord - center, hsize - radius, radius), 0.0, 1.0);
    fragColor = vec4(a,a,a,1.0);
}
