void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0,uv).rgb;

    float luminance = 0.2126*col.r + 0.7152*col.g + 0.0722*col.b;

    // convert to 4 shades
    luminance *= floor((luminance*255.0)/64.0)/4.0;

    // scanlines & noise
    luminance *= mod(fragCoord.y,2.0)/2.0;   
    float noise = pow(texture(iChannel1,vec2(iTime*100.0,uv.y*10.0)).r,-1.0);
    luminance *= noise;

    // tint (so green-ish rather than pure greenscale)
    col = vec3(luminance*luminance*0.25,luminance,luminance*0.33);

    // vignette
    float dist = 1.0-length(uv-vec2(0.5,0.5));
    dist = pow(dist,0.25);

    col *= dist;

    fragColor = vec4(col,1.0);
}
