void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    vec2 u = uv - 0.24;

    float e = iTime*0.5,
          a = atan(u.x,u.y),
          k = mod(a+e +3.14,6.28)-3.14, g;  

    u = uv - vec2(.7,.32);
    a = atan(u.x,u.y);
    g = mod(a-e +3.14,6.28)-3.14;

    fragColor = vec4((k-g)*.2);
}
