// modified from: https://www.shadertoy.com/view/lsyfWD
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float speed = .2;
    float scale = 0.003;
    vec2 p = fragCoord * scale;   
    for(int i=1; i<10; i++){
        p.x+=0.3/float(i)*sin(float(i)*3.*p.y+iTime*speed)+iMouse.x/1000.;
        p.y+=0.3/float(i)*cos(float(i)*3.*p.x+iTime*speed)+iMouse.y/1000.;
    }
    // scale = 0.03;
    // p = fragCoord * scale;
    float r=cos(sin(p.x)-sin(p.y)+iTime*speed*2.)*.3+.5;
    float g=sin(cos(p.x)+cos(p.y)-iTime*speed*2.)*.3+.5;
    float b=(sin(cos(p.x)*cos(p.y)+iTime*speed*2.)-cos(sin(p.x)*sin(p.y)+iTime*speed*2.))*.3+.5;
    // r = 0.;
	// g = 0.;
    // b = 0.;
    vec3 color = vec3(r,g,b);
    fragColor = vec4(color,1);
}
