//This is based on https://www.shadertoy.com/view/3d2BWD

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (100.*fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    //uv *= 30.*abs(1.1 + sin(iTime*0.1));
    float len;
    uv += sin(uv/20.0)/10.0;
    for (int i = 0; i < 10; i++) {
    	len = length(uv);
        uv.x += sin(iTime+sin(uv.y))-len/uv.y;
        uv.y += cos(iTime+cos(uv.x))-len/uv.x;
    }
    float col = cos(len);
    fragColor = vec4(col,col,col,1.0);
}
