// from https://www.shadertoy.com/view/XdfGDH
float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // from https://www.shadertoy.com/view/XdfGDH
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            final_color += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 unblurredColor = texture(iChannel0, uv);

    fragColor = vec4(mix(unblurredColor.rgb, final_color/(Z*Z), unblurredColor.a), 1.0);

    float vignette = smoothstep(1.75, 0.0, length((2.0 * uv) - vec2(1.0)));
    fragColor = mix(vec4(0.0, 0.0, 0.1, 1.0), fragColor, vignette);

    //fragColor = vec4(unblurredColor.a, unblurredColor.a, unblurredColor.a, 1.0);
}

const int RAY_STEPS = 256;
const float HALF_PI = 3.14159 * 0.5;

const float FOCAL_LENGTH = 16.0;
const float FOCAL_RANGE = 16.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}



void sceneMap3D(vec3 pos, out float t, out int hitObj, vec3 lightPos)
{
    // back wall
    t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));  
    hitObj = 0;  // 0 is the back wall

    float t2;

    if((t2 = plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))) < t)  // right wall
    {
        t= t2;
        hitObj = 1;
    } 
    if((t2 = plane(pos, vec4(1.0, 0.0, 0.0, 5.0))) < t)  // left wall
    {
        t= t2;
        hitObj = 2;
    }
    if((t2 = plane(pos, vec4(0.0, -1.0, 0.0, 7.5))) < t)  // celling 
    {
        t= t2;
        hitObj = 3;
    }
    if((t2 = plane(pos, vec4(0.0, 1.0, 0.0, 2.5))) < t)  // floor
    {
        t= t2;
        hitObj = 4;
    }
    if((t2 = box(rotateY(pos + vec3(-2.2, 0.5, -3), -27.5 * 3.14159 / 180.0), vec3(1.5, 3.0, 1.5))) < t)  // large box
    {
        t= t2;
        hitObj = 5;
    }
    if((t2 = box(rotateY(pos + vec3(2.2, 0.5, -3), 27.5 * 3.14159 / 180.0), vec3(1.5, 1.5, 1.5))) < t)  // small box
    {
        t= t2;
        hitObj = 6;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));

    t = min(t, plane(pos, vec4(-1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(0.0, -1.0, 0.0, 7.5)));
    t = min(t, plane(pos, vec4(0.0, 1.0, 0.0, 2.5)));
    t = min(t, box(rotateY(pos + vec3(-2.2, 0.5, -3), -27.5 * 3.14159 / 180.0), vec3(1.5, 3.0, 1.5)));
    t = min(t, box(rotateY(pos + vec3(2.2, 0.5, -3), 27.5 * 3.14159 / 180.0), vec3(1.5, 1.5, 1.5)));

    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    // No intersection
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightDir, vec3 view)
{
    switch(hitObj)
    {
        case 0:  // back wall
        vec3 color0 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color0;
        break;

        case 1:  // right wall 
        vec3 color1 = vec3(1.0, 0.0, 0.0) * dot(lightDir, n);
        return color1;
        break;

        case 2:  // left wall
        vec3 color2 = vec3(0.0, 1.0, 0.0) * dot(lightDir, n);
        return color2;
        break;

        case 3:  // celling
        vec3 color3 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color3;
        break;

        case 4:  // floor
        vec3 color4 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color4;
        break;

        case 5:  // large box
        vec3 color5 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color5;
        break;

        case 6:  // small box
        vec3 color6 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color6;
        break;

        case -1:
        return vec3(0., 0., 0.);
        break;

    }
    return vec3(0.0);
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
	float t;
    int hitObj;

    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    return Intersection(t, surfaceColor, isect, hitObj);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{

    eye = vec3(0.0, 3.0, -15.0);
    ref = vec3(0.0, 3, 0.0);

    vec3 look = normalize(ref - eye);
    vec3 right = normalize(cross(look, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, look);

    float tanFovV = tan(3.14159 * 0.125);
    float len = distance(ref, eye);
    float aspect = float(iResolution.x) / float(iResolution.y);

    vec3 V = up * len * tanFovV;
    vec3 H = right * len * aspect * tanFovV;

    vec3 p =ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Light
	vec3 lightPos = vec3(0.0, 7.3, -1.0);

    //Normalized pixel coordinate (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    //[-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    // Cast ray
    raycast(uv2, dir, eye, ref);

    // Intersection
    Intersection isect;

    isect = sdf3D(dir, eye, lightPos);


    float distAlongCamZ = abs(dot(normalize(ref - eye), (isect.p - eye)));

    float focalLength = 20.0 + 10.0 * sin(iTime);

    float dofZ = min(1.0, abs(distAlongCamZ - focalLength) / FOCAL_RANGE);
    dofZ = pow(dofZ, 0.5);

    fragColor = vec4(isect.color * step(0.0, isect.t), dofZ);
    float fogT = smoothstep(50.0, 100.0, distance(isect.p, eye));
    //Output color
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);

}
