void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 Pos = fragCoord.xy;
    Pos.x /= iResolution.x;

    // Time varying pixel color
    vec4 solidColor = vec4(0.0, 0.0, 0.0, 1.0);
    if (Pos.x > 0.5)
    {

    solidColor.r = Pos.x;

    }


    fragColor = solidColor;
}
