void mainImage(out vec4 O, vec2 C)
{
    O-=O;
	vec2 q=C.xy/iResolution.y-.4;
    vec3 p,d=normalize(vec3(q,-2.-dot(q,q)*3.)).xzy;
    d.xz+=sin(d.xz*9.-iTime)*8.;
    float t,e,i;
    for(i=0.;i<30.;i++){
        p=d*t;
        t+=e=(length(vec3(mod(p.xz,4.)-2.,p.y+2.)))*.05;
    }
    O=vec4(cos(vec3(5,6,9)+d)/30./e,1);
}
