void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float iterations = 20.0;
    vec3 col;
    col = vec3(1.0,1.0,1.0);
    float iterated = 0.0;

    for (float i = 0.0; i <= iterations; i++)
    {

        int x = int(i);
        float colony = sin(uv.x+( iTime*0.5*(float(x)*0.05))+i)+(((sin(iTime))*0.5*(float(x)*0.05))+0.5);
        if (colony >= uv.y-0.05 && colony <= uv.y+0.05 && iterated == 0.0)
    	{
            if (x % 4 == 1)
            {
                col *= 0.5;
    			col.r = 0.5;
            	col.b = 0.8;
                iterated = 1.0;
            }
            else if (x % 4 < 1)
            {
                col *= 0.5;
    			col.b = 0.5;
           		col.g = 0.8;
                iterated = 1.0;
            }
            else if (x % 4 > 1)
            {
                col *= 0.5;
    			col.g = 0.5;
                col.r = 0.8;
                iterated = 1.0;
            }
    	}

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
