const float pi = 3.141592653589;
float mmod(float a, float b) { return a - floor(a/b); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 c1 = vec2(0.5,0.5);
    float n = 2.0 + 4.0*sin(pi*mmod(iTime,1.0));
    float a = cos(length(uv - c1) * 2.0 * (n + 2.0) * 1.0 * pi) * sin(length(uv - c1) * 2.0 * n * 1.0 * pi);
    vec3 col = vec3(0,1,0) * a;
    fragColor = vec4(col,1.0);
}
