vec3 hsv2rgb_smooth( in vec3 c )
{
	vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
	return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 spiral(vec2 uv,float zoom, float len)
{
    float d = length(uv) * zoom;
    //d = log(d);
    float a = atan(uv.x, uv.y) / 3.14159 * .5;
    float v = 1.0 - abs(fract(d - a) * 2.0 - 1.0);
    float x = d - a;
    float l = a + floor(x)+ 1.0;
    v *= step(l, len);
    return vec2(v,l);	// distx , disty
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float t = fract(iTime*0.4)*5.0;

	vec2 dist = spiral(uv,5.0,t);
    float v = dist.x;
    float l = dist.y;

	v = smoothstep(0.5-0.05,0.5+0.05,v);
    vec3 col = hsv2rgb_smooth(vec3(l,1.0,1.0)) * v;

    if (iMouse.z>0.5)
        col = vec3(v*1.0-l/t);

    fragColor = vec4(col, 1.0);
}
