float rand(vec2 p) {
    return fract(sin(dot(p, vec2(312233.043, 2893121.34)))*3213.43242);
}

float noise(float p) {
    float i = floor(p);
    float f = fract(p);
    return mix(rand(vec2(i)), rand(vec2(i+1.0)), f);
}

float fbm (float p) {
    float v = 0.0;
    float amp = .5;
    float freq = 0.;

    for(int i = 0; i < 8; i++) {
        v += amp * noise(p);
        p *= 2.;
        amp *= .5;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.yy;
    vec2 op = p;

    float cy = floor(iTime);

    vec3 col = vec3(0.0);
    for(float z=-2.0; z<=2.0; z+=1.0) {
        p.x -= 1.0/(1.0-exp(-1.0*sin(iTime+z*0.02)));
        p.y -= fbm(iTime*0.1)*0.1;

        vec2 q = fract(p*15.) - 0.5;
        vec2 id = floor(p*15.);

        for(float y=-1.0; y<=1.0; y+=1.0) {
            for(float x=-1.0; x<=1.0; x+=1.0) {
                vec2 r = q + vec2(x, y);
                vec2 rid = id + vec2(x, y);

                r.x -= rand(rid+vec2(z))-0.5;
                r.y -= -sin(iTime*2.0+rand(vec2(rid.x)))*1.5*rand(vec2(z));
        		r.y -= -fbm(iTime*1.0+rid.x+rid.y+z)*0.5;

                if(rand(rid+vec2(z)) > 0.2) {
                    col += smoothstep(0.1, 0.0, length(r)-0.15*rand(id))/3.0*vec3(0.8, 0.9, 1.0);
                }
            }
        }
    }


	float sigT = 1.0/(1.0+exp(-sin(iTime)));
    col = mix(col, vec3(0.95, 1.0, 1.0), length(op)*(0.4+rand(op+sin(iTime))*0.1));

    // Output to screen
    fragColor = vec4(col,1.0);
}
