highp uint pack4U8ToUInt(in highp uint v1, in highp uint v2, in highp uint v3, in highp uint v4) {
  return ((v1 & 0xFFu) << 24) | ((v2 & 0xFFu) << 16) | ((v3 & 0xFFu) << 8) | (v4 & 0xFFu);
}

highp float pack4U8ToFloat(in highp uint v1, in highp uint v2, in highp uint v3, in highp uint v4) {
  //return uintBitsToFloat(pack4U8ToUInt(v1, v2, v3, v4));
  // return uintBitsToFloat(((v1 & 0xFFu) << 24) | ((v2 & 0xFFu) << 16) | ((v3 & 0xFFu) << 8) | (v4 & 0xFFu));
  return uintBitsToFloat((v4 & 0xFFu));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//float v = pack4U8ToFloat(0u, 0u, 2u, 25u);
    highp uint v3 = 2u;
    highp uint v4 = 25u;
    highp float v = uintBitsToFloat((v3 << 8u) | v4);
    fragColor = vec4(v == 0.0 ? 0 : 1, 0, 0, 1);
}
