    U = vec2( 2.5*length(U), .9*atan(U.y,U.x) );
    //float a=.48, b=2.35, c = ceil(8.*(U.x-a)/(b-a));
    float c = ceil(4.28*(U.x-.48));
    O = ( c > 0. && c < 9.
            ? U.y += c * .03*iTime, //iMouse.x/R.x,
              U *= mat2(1,1,-1,1)/sqrt(2.),
              U = abs(fract(U)-.5),
