void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float B = 0.1f;

    fragColor = texture(iChannel0, uv);

    float r = 2.f * fragColor.r * (1.f - B) + sqrt(fragColor.r) * (2.f * B - 1.f);
    float g = 2.f * fragColor.g * (1.f - B) + sqrt(fragColor.g) * (2.f * B - 1.f);
    float b = 2.f * fragColor.b * (1.f - B) + sqrt(fragColor.b) * (2.f * B - 1.f);

    fragColor = vec4(r, g, b, 1.f);
}
