#define PI 3.14159

mat3 Rotate(vec3 angles)
{
    vec3 c = cos(angles);
    vec3 s = sin(angles);

    mat3 rotX = mat3( 1.0, 0.0, 0.0, 0.0,c.x,s.x, 0.0,-s.x, c.x);
    mat3 rotY = mat3( c.y, 0.0,-s.y, 0.0,1.0,0.0, s.y, 0.0, c.y);
    mat3 rotZ = mat3( c.z, s.z, 0.0,-s.z,c.z,0.0, 0.0, 0.0, 1.0);

    return rotX*rotY*rotZ;
}

vec3 mod289(vec3 x)
{
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x)
{
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x)
{
  return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

vec3 fade(vec3 t) {
  return t*t*t*(t*(t*6.0-15.0)+10.0);
}

// Classic Perlin noise
float cnoise(vec3 P)
{

  vec3 Pi0 = floor(P); // Integer part for indexing
  vec3 Pi1 = Pi0 + vec3(1.0); // Integer part + 1
  Pi0 = mod289(Pi0);
  Pi1 = mod289(Pi1);
  vec3 Pf0 = fract(P); // Fractional part for interpolation
  vec3 Pf1 = Pf0 - vec3(1.0); // Fractional part - 1.0
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute(permute(ix) + iy);
  vec4 ixy0 = permute(ixy + iz0);
  vec4 ixy1 = permute(ixy + iz1);

  vec4 gx0 = ixy0 * (1.0 / 7.0);
  vec4 gy0 = fract(floor(gx0) * (1.0 / 7.0)) - 0.5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(0.0));
  gx0 -= sz0 * (step(0.0, gx0) - 0.5);
  gy0 -= sz0 * (step(0.0, gy0) - 0.5);

  vec4 gx1 = ixy1 * (1.0 / 7.0);
  vec4 gy1 = fract(floor(gx1) * (1.0 / 7.0)) - 0.5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(0.0));
  gx1 -= sz1 * (step(0.0, gx1) - 0.5);
  gy1 -= sz1 * (step(0.0, gy1) - 0.5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = taylorInvSqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = taylorInvSqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x); 
  return max(0., 2.2 * n_xyz);
}

float fbm(vec3 p) {
    p *= .5;

 	float t = 1.;
    float s = 1.;
    float a = 0.;
    for (int i = 0; i < 4; i++) {
     	a += t * cnoise(p * s);
        t *= 0.7;
        s *= 1.9;
    }

    return a;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float opUnion(float d1, float d2) {
 	return min(d1, d2);   
}

float opIntersect(float d1, float d2) {
 	return max(d1, d2);   
}

float opComp(float d1, float d2) {
    return max(d1, -d2);
}


float FOG_AMOUNT = 0.2;
float map(vec3 p) {

    p.z -= 15.;
    p.x -= 5.;
    p += vec3(.5, .5, 0.);
    p.y = mod(p.y, 1.) - .5;

 	float d1 = sdRoundBox(p, vec3(.5), 0.01);
    float d2 = sdRoundBox(p, vec3(.4, .4, 1.), 0.03);
    float d3 = sdRoundBox(p, vec3(.4, 1., .4), 0.03);
    float d4 = sdRoundBox(p, vec3(1., .4, .4), 0.03);


    float d = opComp(d1, d2);
    d = opComp(d, d3);
    d = opComp(d, d4);

   	p.y = mod(p.y, .1) -.05;
    d = min(d, length(p) - .2);

    return d;
}

void applyFog( inout vec3  rgb, in float dist )
{
    float fogAmount = 1.0 - exp( -dist*FOG_AMOUNT );
    vec3  fogColor  = vec3(.05, .05, .1);
    fogColor = vec3(0);
    rgb = mix( rgb, fogColor, fogAmount );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;


    //Compute rotation matrix from mouse coordinates
    vec3 angles = vec3((iMouse.xy/iResolution.xy)*PI,0);
    angles.xy *= vec2(2.0,1.0);
    angles.y = clamp(angles.y,0.0,PI/2.0);
    mat3 rotate = Rotate(angles.xyz);
    //rotate = Rotate(vec3(iTime));

    //Calculate ray direction
    vec3 o = vec3(0., 0., -3.);
    vec3 r = normalize(vec3(uv, 2.));

    //mouse rotation
   	o = rotate * o;
    r = rotate * r;
    //o.z += iTime;
    o.y += .9;


    //raymarch object
    float d2 = 0.;
    for (int i = 0; i < 50; i++) {        
        vec3 p = o + r * d2;
        d2 += map(p);
        //d += p.y + .5;
    }

    vec3 col = vec3(.7, .3, .9);
    applyFog(col, d2);

    //trace clouds
    float d = 0.;
    float step_size = .8;
    vec3 c = vec3(0.);
    float a = 0.;
    for (int i = 0; i < 35; i++) {
        if (float(i) > d2) break;
     	vec3 p = o + r * (float(i) * step_size);

        float n = fbm(p*.8 + iTime * .1);
        n += .1;
        vec3 c_t = n * vec3(.9, .8, 1.);
        float t =  1.2 * n * mix(0., 1., smoothstep(.0, 1., -p.y +
                                         3.*cnoise(vec3(iTime*.1 + p.xz*.3, 0.))));
        c += (1. - a) * t * c_t * step_size;   	
        a += t * (1. - a);
        if (a >= 1.) break;

    }

	//c = 0.3 * exp(c);
    col = mix(vec3(0.45, 0.6, .9) , col, 1. - c);//vec3(.1, .1, 1.) * c;

    col = pow(col, vec3(1./2.2));
    fragColor = vec4(col,1.0);
}
