// GLSL version of https://twitter.com/baldand/status/1100746090763247616

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/iResolution.x;
	float l = length(uv);
    float a = atan(uv.y,uv.x)+6.+iTime;
	float b = 1.256;
	float c = clamp((l*pow(cos(floor(.5+a/b)*b-a),4.)-.1)/-.005,0.,1.);
	float s = c*c*(3.-2.*c);
	vec3 v = s*vec3(.9,.9,0.)*(1.-l*3.);
    fragColor = vec4(v,1.0);
}
