vec3 violet = vec3(238./255.,130./255.,238./255.);
//./255.
vec3 blue = vec3(0.,0.,1.);
vec3 green = vec3(124./255.,252./255.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 orange = vec3(255./255.,165./255.,0.);
vec3 red = vec3(1.,0.,0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
   	uv.x -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    uv*= sin(iTime); 
    uv.x *= sin(iTime);
    float r = 0.1;
    float d = length(uv);

    float c5 = smoothstep(d,d+0.02,0.6) ;
    float c4 = smoothstep(d,d+0.02,0.5) ;
    float c3 = smoothstep(d,d+0.02,0.4) ;
    float c2 = smoothstep(d,d+0.02,0.3) ;
    float c1 = smoothstep(d,d+0.02,0.2) ;
    float c = smoothstep(d,d+0.02,r) ;

    vec3 col = red * c5;

    col -= red * c4;
    col += orange * c4;

    col -= red * c3;
    col -= orange * c3;
    col += yellow * c3;

    col -= red * c2;
    col -= orange * c2;
    col -= yellow * c2;
    col += green * c2;


    col -= red * c1;
    col -= orange * c1;
    col -= yellow * c1;
    col -= green * c1;
    col += blue * c1;

    col -= red * c;
    col -= orange * c;
    col -= yellow * c;
    col -= green * c;
    col -= blue * c;

    col += vec3(0.) * c;


	fragColor = vec4(col,1.);
}
