/* ************************************************************************************* 

Sine & Cosine Waves

Basic tutorial 12
by Arthur Stammet, 03.2019

inspired by The Art of Code : https://www.youtube.com/watch?v=jKuXA0trQPE

************************************************************************************* */

float Bar (float t, float b1, float b2, float blur) {
    float s1 = smoothstep(b1 - (blur/2.), b1 + (blur/2.), t); 	// first ramp
    float s2 = smoothstep(b2 + (blur/2.), b2 - (blur/2.), t); 	// second ramp
    return s1 * s2; 											// build ramp
}

float Rectangle (vec2 uv, float L, float R, float B, float T, float blur ) {
    float b1 = Bar(uv.x, L, R, blur); 	// build Left-Right Limits
    float b2 = Bar(uv.y, B, T, blur); 	// build Bottom-Top Limits
    return b1 * b2;
}

float sineWave (float x, float s, float f, float a) {
    float w = sin(s+x*f)*a;
    return w;
}

float cosineWave (float x, float s, float f, float a) {
    float w = cos(s+x*f)*a;
    return w;
}

float Waves4 (float x, float p, float f, float a) {
    float w = sin(p+x*f)*a;
    return w;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set the center to the middle of the screen and make the pixels quadratic
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; 

    // change values here --------------------------------------------------------------

	float freq = 14. + sin(iTime * 0.1) * 10.;
    float amp = sin(iTime * 0.15) * 0.4;    
    float phase = iTime;    
    float blur = 0.03;    

    // dimensions of the base rectangle (before y-distortion)
    float width = 1.5;
    float height = 0.04;

    // ---------------------------------------------------------------------------------

	float x = p.x;
    float modS = sineWave( x, phase, freq, amp);
    float modC = cosineWave( x, phase, freq, amp);
    float yS = p.y - modS;
    float yC = p.y - modC;

    vec3 back = vec3 ( 0., 0., 0. ); // colorized screen (animated)
    vec3 sinWave = vec3 ( 0., 1., 0. ); // green
    vec3 cosWave = vec3 ( 0., 0., 1. ); // blue
	sinWave *= Rectangle( vec2(x,yS), -width/2., width/2., -height/2., height/2., blur);
	cosWave *= Rectangle( vec2(x,yC), -width/2., width/2., -height/2., height/2., blur);

    fragColor = vec4(back + sinWave + cosWave, 1.0);
}
