#define M_PI 3.1415927

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
        uv = (2.*U - R ) / R.y;
    float ring = sin (3.14* length(uv)                     *8.),
      rotation = sin((3.14*(atan(uv.y/uv.x)+M_PI/2.0)/M_PI)*8.);

    O = vec4 ( mix(ring * rotation / fwidth(ring * rotation), .5, 1./(exp(-1000.*(length(uv)-1.))+1.)) );  
}
