const float spreadRate = 0.3;	// How much the virus spreads per day
const float recoverRate = 0.04;	// How much each person recovers per day
const int peopleCount = 30;		// Total amount of people
const int timeLength = 120;		// Length in days

float hash(float n){return fract(sin(n) * 43758.5453123);}

void calculateVirusValues(inout float values[timeLength]) {
    float healthPerson[peopleCount];
    int isInfected[peopleCount];
    int hasBeenInfected[peopleCount];

    for (int i = 0; i < peopleCount; i++) {
		healthPerson[i] = 1.0;
        isInfected[i] = 0;
    }

    healthPerson[0] = 0.0;
    isInfected[0] = 1;
    hasBeenInfected[0] = 1;

    for (int i = 0; i < timeLength; i++) {
        values[i] = 0.0;

        for (int j = 0; j < peopleCount; j++) {
            if (isInfected[j] == 0) {
            	continue;    
            }

            float getInfected = clamp(floor(hash(float(i+j)) + spreadRate), 0.0, 1.);

            if (isInfected[j] == 1) {
				healthPerson[j] += recoverRate;
                hasBeenInfected[j] = 1;

                int nextPersonId = int(hash(float(i-j*255)) * float(peopleCount));

                if (hasBeenInfected[nextPersonId] == 0) {
                    healthPerson[nextPersonId] = 1.0 - getInfected;
                    isInfected[nextPersonId] = int(getInfected);
                }
            } 

            if (healthPerson[j] >= 1.0) {
				healthPerson[j] = 1.0;

                isInfected[j] = 0;
            }
        }

        for (int j = 0; j < peopleCount; j++) {
			values[i] += float(isInfected[j]);
        }
    }
}

float drawLine(vec2 uv, vec2 p1, vec2 p2, float pixelSize) {
  float a = abs(distance(p1, uv));
  float b = abs(distance(p2, uv));
  float c = abs(distance(p1, p2));

  if ( a >= c || b >=  c ) return 0.0;

  float p = (a + b + c) * 0.5;

  // median to (p1, p2) vector
  float h = 2. / c * sqrt( p * ( p - a) * ( p - b) * ( p - c));

  return 1.0 - smoothstep(pixelSize, pixelSize * 2., h);
}

float graphValues(vec2 uv, float pixelSize) {
	vec2 xy = vec2(timeLength, peopleCount);
    float values[timeLength];
    calculateVirusValues(values);

    float result = 0.0;

    for (int i = 0; i < timeLength; ++i) {
        float nextPeopleValue = values[(i + 1) % timeLength];
        float nextTimeValue = float(i + 1);

        float peopleValue = values[i];
        float timeValue = float(i);

        peopleValue = peopleValue;
        nextPeopleValue = nextPeopleValue;

        if (i == timeLength-1) {
			nextPeopleValue = peopleValue;
        }

		result = max(result, drawLine(uv, vec2(timeValue, peopleValue) / xy, vec2(nextTimeValue, nextPeopleValue) / xy, pixelSize));
    }

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float pixelSize = 1.0 / min(iResolution.x, iResolution.y);
    float graph = graphValues(uv, pixelSize);

    // Time varying pixel color
    vec3 col = vec3(graph);

    // Output to screen
    fragColor = vec4(col,1.0);
}
