void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define RAY_STEPS 256

const int AO_SAMPLES = 256;
const float AO_DIST = 0.15;
const float FIVETAP_K = 2.0;

// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 1.0;
// Toggle this to affect how easily the subsurface glow propagates through an object
#define ATTENUATION 0

const float FOVY = 3.141569 * 0.25;

struct Intersection {
    float t;
    vec3 p;
    vec3 color;
    int object;
};

void coordinateSystem(const vec3 v1, out vec3 v2, out vec3 v3) {

    if (abs(v1.x) > abs(v1.y)) {
        v2 = vec3(-v1.z, 0, v1.x) / length(v1.xz);
    }
    else {
        v2 = vec3(0, v1.z, -v1.y) / length(v1.yz);
    }
    v3 = cross(v1, v2);
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

//sphere sdf
float sphere(vec3 p, float r, vec3 c) {
 	return distance(p, c) - r;   
}

//box sdf
float box(vec3 p, vec3 b) {
    return length(max(abs(p) - b, 0.0));
}

//plane sdf
float plane(vec3 p, vec4 n) {
    return dot(p, n.xyz) + n.w;
}

#define LIGHT_POS vec3(0., 5., 4.)
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 5.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 2.0))
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * 3.14159 / 180.0), vec3(1., 2., 1.))
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * 3.14159 / 180.0), vec3(1., 1., 1.))

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define CEILING 3
#define FLOOR 4
#define TALL_BOX 5
#define SMALL_BOX 6

void testScene(vec3 pos, out float t, out int obj) {
    t = BACK_WALL_SDF;
    obj = BACK_WALL;

    float t2;
    if ((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
        obj = LEFT_WALL;
    }
    if ((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
        obj = RIGHT_WALL;
    }
    if ((t2 = CEILING_SDF) < t) {
        t = t2;
        obj = CEILING;
    }
    if ((t2 = FLOOR_SDF) < t) {
        t = t2;
        obj = FLOOR;
    }
    if ((t2 = TALL_BOX_SDF) < t) {
        t = t2;
        obj = TALL_BOX;
    }
    if ((t2 = SMALL_BOX_SDF) < t) {
        t = t2;
        obj = SMALL_BOX;
    }
}

float testScene(vec3 pos) {
 	float t = BACK_WALL_SDF;

    float t2;

    t = min(t, LEFT_WALL_SDF);
    t = min(t, RIGHT_WALL_SDF);
    t = min(t, CEILING_SDF);
    t = min(t, FLOOR_SDF);
    t = min(t, TALL_BOX_SDF);
    t = min(t, SMALL_BOX_SDF);

    return t;
}

vec3 computeNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(testScene(pos + epsilon.yxx) - testScene(pos - epsilon.yxx),
                          testScene(pos + epsilon.xyx) - testScene(pos - epsilon.xyx),
                          testScene(pos + epsilon.xxy) - testScene(pos - epsilon.xxy))
                     );
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
 	t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        testScene(pos, m, hitObj);
        if (m < 0.01) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = testScene(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t) {
    return softShadow(dir, origin, min_t, 6.0);
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    #if ATTENUATION
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    #endif
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thickness) {
    float t;
    float lambert = dot(lightVec, n);
    vec3 mat = vec3(0., 0., 0.);

    switch(hitObj) {
        case BACK_WALL:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case LEFT_WALL:
        mat = vec3(1., 0., 0.);
        break;
        case RIGHT_WALL:
        mat = vec3(0., 1., 0.);
        break;
        case CEILING:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case FLOOR:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case TALL_BOX:
        vec3 color = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(n, lightVec)) * shadow(lightVec, p, 0.1);
        return color + vec3(1.0, 0.67, 0.67) * subsurface(lightVec, n, view, thickness) * vec3(1.0, 0.88, 0.7);
        break;
        case SMALL_BOX:
        mat = vec3(0.8, 0.8, 0.8) + subsurface(lightVec, n, view, thickness) * vec3(0.8, 0.8, 0.8);
        break;
        case -1:
        mat = vec3(0., 0., 0.);
    }
    return mat * lambert;
}

vec2 random2(vec3 p) {
    return fract(sin(vec2(p.x, p.x + 1.0)) * vec2(43758.5453123, 22578.1459123));
}

float computeAO(vec3 p, vec3 n, float dist) {
    float aoSum = 0.0;
    // Sample a few points in the hemisphere around n at p
    vec3 t, b;
    // Make a tangent and bitangent vector
    coordinateSystem(n, t, b);
    for(int i = 0; i < AO_SAMPLES; ++i) {
        // Generate a pair of random [0, 1] floats
        vec2 xi = random2(p + float(i) * 203.1);
        // Convert the xi pair to a vector in the hemisphere
        float len = sqrt(xi.y);
        float rx = len * cos(6.2831 * xi.x);
        float ry = len * sin(6.2831 * xi.x);
        float rz = sqrt(1.0 - xi.y); // z = sqrt(1 - x*x - y*y)
        vec3 dir = vec3(rx * t + ry * b + rz * n);
        aoSum += step(0.0, testScene(p + dir * dist));
    }
    return aoSum / float(AO_SAMPLES);
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - testScene(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

Intersection sdf3D (vec3 dir, vec3 eye, vec3 lightPos) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);
    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(lightPos - isect); 

    float thickness = fiveTapAO(isect, -nor, FIVETAP_K);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);
    if (hitObj == -1) {
        surfaceColor = vec3(0., 0., 0.);
    }

    // five tap
    float ao = fiveTapAO(isect, nor, FIVETAP_K);

    // both
    surfaceColor *= ao;

    return Intersection(t, isect, surfaceColor, hitObj);
}


// returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
 	vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0, 1, 0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    vec3 ray = normalize(p - eye);
    ray.x *= -1.; // flip x-axis
    return ray;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 5, -15);
    vec3 ref = vec3(0, 3, 0);

    vec3 rayDir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(rayDir, eye, LIGHT_POS);
    fragColor = vec4(isect.color, 1.);
}
