float circle(in vec2 _st, in float _radius) {
    vec2 l = _st-vec2(0.5);
    return smoothstep(_radius+9./iResolution.y,
                         _radius-9./iResolution.y,
                         dot(l,l)*4.0);
}

float circlePattern(vec2 st, float r) {
    return  circle(st+vec2(0.,-.5), r)+
            circle(st+vec2(0.,.5), r)+
            circle(st+vec2(-.5,0.), r)+
            circle(st+vec2(.5,0.), r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    uv *= 4.0;
    uv = fract(uv);

    fragColor = vec4(0.9529411765,0.8117647059,0.7803921569,1.0) * circlePattern(uv, 1.0 * sin(iTime) * 0.5 + 0.5);
}
