void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.0);

    vec3 sq_col = vec3(0.0, 0.0, 0.0);

    vec2 size = vec2(.1);
	vec2 pos = vec2(0.0);

    vec2 bl2 = pos;
	vec2 tr2 = 1. - pos - size/.5;

    vec2 bl = step(bl2 - size/.5,st); // bottom-left
    vec2 tr = step(tr2,1.-st);// top-right
    sq_col += vec3(bl.x * bl.y * tr.x * tr.y);

    color = vec3(sq_col);

    fragColor = vec4(color,1.0);
}

