const float Pi = 3.1415926;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv2center = uv * 2.0 - 1.0;
    float dist = length(uv2center);
    float bgColor = mod(texture(iChannel0, uv / 10.0).r + iTime / 20.0, 2.0);
    if (bgColor > 1.0) {
        bgColor = 1.0 - bgColor + 1.0;
    }
    fragColor = vec4(0, bgColor / 6.0, bgColor / 4.0, 1.0);
    float bdf = max(sin(iTime + uv.y * 10.0) / 10.0, sin(iTime + uv.y * 10.0 + Pi) / 10.0);
    fragColor.g *= min(bdf * 10.0 + 0.2, 1.0);
    fragColor.b *= min(bdf * 10.0 + 0.2, 1.0);
    float rotate = sin(iTime / 10.0);
    float maxDiff = sin(iTime + uv.y * 10.0) / 10.0;
    float exceptedDist = texture(iChannel0, uv / 2.0 + vec2(0.0, rotate)).r;
    float diff = abs(dist - exceptedDist);
    if (diff < maxDiff) {
        float dc = 1.0 - diff * 10.0;
        fragColor = (vec4(vec3(0.0, dc, 1.0) * dc, 1.0) + fragColor) / 2.0;
    }
    rotate = sin(iTime / 10.0 + Pi);
    maxDiff = sin(iTime + uv.y * 10.0 + Pi) / 10.0;
    exceptedDist = texture(iChannel0, uv / 2.0 + vec2(0.5, rotate)).r;
    diff = abs(dist - exceptedDist);
    if (diff < maxDiff) {
        float dc = 1.0 - diff * 10.0;
        fragColor = (vec4(vec3(0.0, dc, 1.0) * dc, 1.0) + fragColor) / 2.0;
    }
    float height = texture(iChannel0, vec2(floor(uv.x * 50.0) / 50.0, iTime / 50.0)).r;
    if (mod(uv.x, 1.0 / 50.0) > 0.3 / 50.0 && ceil(height * 30.0 / 4.0) / 30.0 >= uv.y && mod(uv.y, 1.0 / 30.0) > 0.2 / 30.0) {
        float amplify = texture(iChannel0, vec2(floor(uv.y * 30.0) / 30.0, floor(uv.x * 50.0) / 50.0 + iTime / 500.0)).r;
        if (amplify > 0.75) {
            fragColor = vec4(vec3(0.0, 0.2, 0.3) * (uv.y * 4.0 + 0.5 + amplify / 1.5), 1.0);
        } else {
        	fragColor = vec4(vec3(0.0, 0.2, 0.3) * (uv.y * 4.0 + 0.5), 1.0);
        }
    }
    fragColor.g *= 2.0;
    fragColor.b *= 2.0;
}
