float box(vec2 st,float size)
{
    st = smoothstep(0.,15./iResolution.y, size-.5 - abs(st-.5));
   	return st.x*st.y;
}

float wave(vec2 st,float n)
{
    st=(floor(st*n)+0.5)/n;
	float d=length(0.-st);
    return (1.+sin(d-iTime*2.))*0.5;
}

float boxWave(vec2 uv,float n)
{
 	vec2 st=fract(uv*n);
    float size=wave(uv,n);
    return box(st,size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
  	fragColor=vec4(boxWave(p,2.),boxWave(p,4.),boxWave(p,8.),1.);
}
