// feedback comes from this one https://www.shadertoy.com/view/XsSfDR

void mainImage(out vec4 O, vec2 U)
{

    float oSpeed = 6. * abs(sin(iTime * 3.) );

    O=texture(iChannel0, fract(U/(iResolution.xy - oSpeed)))+sin(U.xyyy/50.)/(iTime);
    float amnt = 7. * abs(sin(iTime *.1) ) + 6.;

    for(float i = 0.;i< amnt ;i+=1.0){

        vec2 v1 = fract( (U - O.yx) / (iResolution.xy + sin(iTime * 1.) * i ) );
        vec2 v2 = fract((O.xy) / (iResolution.xy + cos(iTime * 2.) * i * 2. ) );
        O += (1. + .1 * sin(iTime * .1) ) * texture(iChannel0, v1);
    	O -= (1. + .1 * sin(iTime * .3) ) * texture(iChannel0, v2);

    }



    O = normalize(.4 * abs(sin(iTime * .2))*texture(iChannel0, fract((U + O.yx)/iResolution.xy)) +.02 * cos(iTime *.4)*O.wxyz);
}
