vec4 colormap(float x) {
    float g = clamp(x, 0.0, 1.0);
    return vec4(1.0, g, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float distUp = length((uv - vec2(0.5, 0.3)) * vec2(1.5, 0.5));
    float distDown = length((uv - vec2(0.5, 0.5)) * vec2(1.5, .5));
    float cutoutDown = clamp(1.0 - distDown / 0.3, 0., 1.);
    float gradX = clamp(1.0 - distUp / 0.3, 0., 1.);
    float gradY = mix(6.0, 0., uv.y + 0.);
    float mask = gradY * cutoutDown;
    float n1 = texture(iChannel0, uv*vec2(0.6)+vec2(0.01*sin(iTime/3.0), 0.1-iTime/3.)).r * 0.3;
    float n2 = texture(iChannel0, uv*vec2(0.4)+vec2(0.001*iTime/5.0, 0.2-iTime/3.)).g * 0.3;
    float n3 = texture(iChannel0, uv*vec2(0.3)+vec2(0.0, 0.4-iTime/3.)).b * 0.3;
    float noise = n1*0.6+n2*0.3+n3*0.05;
    float mask1=(mask+noise*2.0) /3.0;
    float mask2=(mask+noise*2.0) /3.0;
    float mask3=mask*0.5 + noise*0.5;
    mask=(mask1 * 1.2 + mask2 * 0.8) / 2.0 * 0.7 * pow(mask3, 2.0);

    float col = smoothstep(0.2, 0.7, (mask1 + noise) / 2.0);
    fragColor = col * colormap(col);
    //fragColor = vec4(vec3(mask),1.0);
}
