// Created by Alex Kluchikov

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float4 t  =texture(iChannel0,fragCoord/iResolution.xy);
    float4 t10=texture(iChannel0,(fragCoord+float2(1,0))/iResolution.xy);
    float4 t01=texture(iChannel0,(fragCoord+float2(0,1))/iResolution.xy);

    float4 g  =texture(iChannel1,fragCoord/iResolution.xy);

    float4 c;
    c.a=1.0;
    c.g=0.5;
    c.b=0.5+(t01.r-t.r)*10.0;
    c.r=0.5+(t10.r-t.r)*10.0;
	c.rgb=float3(0.2,0.4,0.8);
    c.rgb+=t.rgb;

	float w=clamp((0.8-pow(g.r,4.0))*8.0,0.0,1.0);

    if(!keyToggled(KEY_W))
	    c.rgb=mix(c.rgb, float3(1,0.85,0.6), w);

    if(keyToggled(KEY_A))
    {
        float a=clamp(1.0-t.a,0.0,1.0);
        a*=a*a;
        c.rgb=float3(
            pow(1.0-a,3.0)*6.0,
            1.0-6.0*(a-0.4)*(a-0.4),
            1.0-6.0*(a-0.6)*(a-0.6)
        );

        if(!keyToggled(KEY_W))
		    c.rgb=mix(c.rgb, float3(1,1,1), w);
    }


    fragColor=c;
}

// Created by Alex Kluchikov

#define float3 vec3
#define float2 vec2
#define float4 vec4

const float PI=3.1415926535;

#define KEY_A 65
#define KEY_W 87
#define KEY_0 48

#define keyPressed(key) (texture(iChannel3,float2((float(key)+0.5)/256.0,0.25)).x>0.0)

#define keyToggled(key) (texture(iChannel3,float2((float(key)+0.5)/256.0,0.75)).x>0.0)

float4 Step(float2 fc, float2 res, float2 mc, 
            float time, int fr, sampler2D s0, sampler2D s1)
{
    if(fr==0)
    {
        return float4(0.0);
    }

    float4 a00=texture(s0,(fc+float2(-1,-1))/res);
    float4 a22=texture(s0,(fc+float2( 1, 1))/res);
    float4 a02=texture(s0,(fc+float2(-1, 1))/res);
    float4 a20=texture(s0,(fc+float2( 1,-1))/res);

    float4 a01=texture(s0,(fc+float2(-1, 0))/res);
    float4 a10=texture(s0,(fc+float2( 0,-1))/res);
    float4 a21=texture(s0,(fc+float2( 1, 0))/res);
    float4 a12=texture(s0,(fc+float2( 0, 1))/res);

    float4 a11=texture(s0,(fc+float2( 0, 0))/res);

    float4 g11=texture(s1,fc/res);

    /*
    if(g11.r<1.0)
    {
        a11=float4(0.0);
        a01=a11;
        a10=a11;
        a21=a11;
        a12=a11;
    }
    */

    float4 c;
    float a0=
        (
        +a01.r
        +a10.r
        +a12.r
        +a21.r
		)*sqrt(0.5)
        +(
        +a01.r
        +a10.r
        +a12.r
        +a21.r
		)*1.0;



    float d=(
        a0-a11.g*(4.0+sqrt(0.5)*4.0)
    )/(4.0+sqrt(0.5)*4.0);

    d*=g11.r;

    c.r=a11.r+d;
    c.g=a11.r;
    c.b=a11.g;
    float a=a11.a*4.0+a01.a+a10.a+a12.a+a21.a;
    c.a=max(a/8.0,abs(c.g))*0.999;


    float freq=1.0;
    float l=length(fc-mc);

    float a1=1.5;
    float a2=0.75;
    float a3=0.75;

    if(l<10.0)
    {
        float a=(1.0-(abs(fc.x-mc.x)/10.0))*clamp((fc.y-mc.y)/10.0,0.0,1.0);
        c.r=sin(float(fr)/PI*freq)*a*a*a*a1;
    }

    l=length(fc-float2(250,5)/float2(640,380)*res)/5.0;
    if(l<1.0)
    {
        c.r=sin(float(fr)/PI*freq)*a2;
    }

    l=length(fc-float2(635,120)/float2(640,380)*res)/5.0;
    if(l<1.0)
    {
        c.r=sin(float(fr)/PI*freq)*a3;
    }


//    c.r=fract(fc.x/res.x*3.0)-0.5;
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor=Step(fragCoord, iResolution.xy*1.0, iMouse.xy, 
                  iTime, iFrame, iChannel0, iChannel1);
    if(keyPressed(KEY_0))
    {
        fragColor=float4(0.0);
    }

}

float lv(float2 p0, float2 p1, float2 uv, float w, float a)
{
    float2 dp=normalize(p1-p0);
    float2 dpp=float2(dp.y, -dp.x);
    float l=abs(dot(dpp,uv-p0));
    if((dot(dp,uv-p0)<0.0))
        l=length(uv-p0);
    if((dot(-dp,uv-p1)<0.0))
        l=length(uv-p1);
    return clamp(1.0-(1.0-l/w)*a,0.0,1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1.0,1.0,1.0,1.0);
    float2 res=iResolution.xy;

    fragColor.r*=lv(float2(0,0),float2(res.x,0),fragCoord,8.0,1.0);
    fragColor.r*=lv(float2(0,0),float2(0,res.y),fragCoord,8.0,1.0);
    fragColor.r*=lv(res,float2(res.x,0),fragCoord,8.0,1.0);
    fragColor.r*=lv(res,float2(0,res.y),fragCoord,8.0,1.0);

    float2 s=res/float2(640,380);

    fragColor.r*=lv(float2(100,150)*s,float2(100,275)*s,fragCoord,4.0,0.15);
    fragColor.r*=lv(float2(300,  0)*s,float2(300,275)*s,fragCoord,4.0,0.15);
    fragColor.r*=lv(float2(250,150)*s,float2(500,150)*s,fragCoord,4.0,0.15);
    fragColor.r*=lv(float2(200,150)*s,float2(  0,150)*s,fragCoord,4.0,0.15);
    fragColor.r*=lv(float2( 75,275)*s,float2(150,275)*s,fragCoord,4.0,2.0);
    fragColor.r*=lv(float2( 25,275)*s,float2(  0,275)*s,fragCoord,4.0,2.0);
    fragColor.r*=lv(float2(200,275)*s,float2(500,275)*s,fragCoord,4.0,2.0);
    fragColor.r*=lv(float2(500,225)*s,float2(500,275)*s,fragCoord,4.0,2.0);
    fragColor.r*=lv(float2(500, 50)*s,float2(500,175)*s,fragCoord,4.0,2.0);
}
