void mainImage (out vec4 _, vec2 px)
{
    float d = 1./max(iResolution.x,iResolution.y);
    vec2 uv = (2.*px-iResolution.xy)*d;
    vec2 po = uv*5.;
    vec4 c = vec4(0);
    for (int i = 0; i < 50; ++i)
    {
        vec2 p = po+d*(.5-fract(vec2(i) * vec2(.7548777, .5698403)));
        vec2 s = 2.*fract(p)-1.;
        vec2 a = .2*floor(p);
	    c += step(s.yyyy, vec4(
            s.x,
            abs(s.x-sin(3.14*a.x)),
            sin(3.14*s.x),
            -2));
    }
    _ = c/50.;
}
