// Variant of https://www.shadertoy.com/view/3dlXD4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.x;
    uv *= 9.;
    vec2 ix = floor(uv)-vec2(3.,-2.);
    uv = fract(uv)-.5;
	float l = length(uv);
    float a = atan(uv.y,uv.x)+6.+iTime;
	float b = 6.282/floor(ix.y);
	float s = smoothstep(.2,.205,pow(cos(floor(.5+a/b)*b-a),ix.x)*l);
	vec3 v = vec3(s);
    fragColor = vec4(v,1.0);
}
