vec2 R;
void mainImage( out vec4 f, in vec2 u )
{
    R = iResolution.xy;
    vec2 v = .5*(u - .5*R.xy)/R.y;
    vec3 d = normalize(vec3(v, 1. - dot(v, v) * 4.));
    float s;
    float t = iTime;
 	vec3 b = vec3(4.0, .02, 14.);

    for(int i = 0; i < 64;i++)
    { 
        float D = min(length(mod(vec3(-t*2., 0, 0)-(d*s), b)-.5*b)-.25, 
                      3.0-abs(d.y)*s);
        D = min(D, length(mod(vec3(t*2., 0, 6.2)-(d*s), b)-.5*b)-.25);

        if(D < .01 || s > 85.)
            break;

         s += D;  
    }
    f = vec4(.9) * s / 80.;
}
