vec3 red = vec3(225./255., 95./255., 60./255.);
vec3 pink = vec3(255./255.,192./255.,203./255.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float x = sin(iTime * 1.5 ) ;

    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2(.5 * ratio, .5) ;
    float dist = distance ( shapePos ,uv) ;

    vec2 shapePos2 = vec2(.5 * ratio, .5);
    float dist2 = distance ( shapePos2,uv) ;

    uv -= shapePos;

    //get angle
    float angle = atan(uv.y, uv.x)  +  x;

    float radius = cos(5. * angle  + iTime + x) * .3;
  	float radius2 = cos(5. *  atan(uv.y , uv.x) + iTime  + x) * .3;
    float radius3 = cos(5. *  atan(uv.y , uv.x) + x) * .3;

    radius *= sin(iTime * .5);
   radius2 *= sin(iTime * .5);
    radius3 *= sin(iTime * .5);

    //vec3 color = mix (vec3(0.));
    float value =   1.  - step(radius,dist);
    float value2 = 1.  - step(radius2,dist2);
    float value3 = 1.  - step(radius3,dist2);

    vec3 color = mix(pink,red ,value + value2 + value3);


  //  fragColor = vec4(value + value2 + value3 );
    fragColor = vec4(color  ,1.);

}
