float rect(vec2 uv, vec2 pos, float width, float height)
{
    float square = (step(pos.x - width, uv.x) - step(pos.x + width, uv.x)) *
                   (step(pos.y - height, uv.y) - step(pos.y + height, uv.y));


    return square;
}

mat2 rotate2d(float angle)
{
    return mat2 (sin(angle), -cos(angle), cos(angle), sin(angle));
}

mat2 scale2d(vec2 value)
{
    return mat2 (value.x, 0, 0,  value.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Colors in vec3
    vec3 red = vec3(0.8 ,0.0 ,0.0);
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 yellow = vec3(0.9, 0.9, 0.3);
    vec3 blue = vec3(0.0, 0.2, 1.0);
    vec3 black = vec3(0.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);

    vec2 uv = fragCoord/iResolution.xy;
    vec2 origPos = uv;
	float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    vec2 pos = vec2(0.5*ratio, 0.5);

    //uv *= 3.0;

    //uv = fract(uv);

    uv -= pos ;
    uv *= rotate2d(sin(iTime * 3.0)  );
    uv += pos;
    uv *= scale2d(vec2(1.0, 1.0)); 
    vec3 color = vec3(rect(uv, pos, 0.1, 0.1))*green;

    uv = origPos;
    uv.y *= ratio;
    uv -= pos * 0.5;
    uv *= rotate2d(sin(iTime * 1.0) * 2.0);
    uv += pos;         
    uv = fract(sin(uv) * 50.);
    uv *= 10.0;
    //uv *= scale2d(vec2(1.0, 1.0)); 
    color = mix(color, blue + green, vec3(rect(uv, pos, 0.1, 0.1)));


    fragColor = vec4(color ,1.0);
}
