// Colorized screen
// my step by step tutorial 2
// by Arthur Stammet, 03.2019

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; // set center to the middle of the screen

    // change values here --------------------------------------------------------------

	float red = 1.0; 	// 0.0 <> 1.0
	float green = 0.0; 	// 0.0 <> 1.0
	float blue = 0.0; 	// 0.0 <> 1.0

    // ---------------------------------------------------------------------------------

    vec3 col = vec3( red, green, blue ); // colorized screen (red)	

    fragColor = vec4(col, 1.0);
}
