#define PI 3.14159
float pitch(float p)
{
    return pow(1.059460646483,p)*440.0;
}

vec2 mainSound( in int samp,float t)
{
    float p=(sin(2.0*PI*pitch(-5.0)*t));
    float s=(sin(2.0*PI*pitch(0.0)*t)+sin(2.0*PI*pitch(7.0)*t)+sin(2.0*PI*pitch(12.0)*t));
    return vec2(s*0.3);
}
